+as_density_function <- function(f, eval_env = parent.frame()) {
+  if (is.function(f)) {
+    return(f)
+  }
+
+  if (is.character(f) && length(f) == 1L) {
+    expr <- parse(text = f)[[1L]]
+    return(function(x) eval(expr, envir = list(x = x), enclos = eval_env))
+  }
+
+  if (is.expression(f) && length(f) == 1L) {
+    expr <- f[[1L]]
+    return(function(x) eval(expr, envir = list(x = x), enclos = eval_env))
+  }
+
+  if (is.language(f)) {
+    expr <- f
+    return(function(x) eval(expr, envir = list(x = x), enclos = eval_env))
+  }
+
+  stop("`f` must be a function, expression, call, or character expression.")
+}
