+safe_eval_density <- function(density_fn, x) {
+  values <- tryCatch(
+    density_fn(x),
+    error = function(e) {
+      stop(sprintf("Density evaluation failed: %s", e$message))
+    }
+  )
+
+  if (!is.numeric(values)) {
+    stop("Density function must return numeric output.")
+  }
+  if (length(values) != length(x)) {
+    stop("Density function must be vectorized: output length must equal input length.")
+  }
+  values <- as.numeric(values)
+  if (any(is.na(values))) {
+    stop("Density function returned NA values.")
+  }
+  values
+}
