+make_log_density_function <- function(density_fn, log_input = FALSE) {
+  if (!is.logical(log_input) || length(log_input) != 1L || is.na(log_input)) {
+    stop("`log_input` must be TRUE or FALSE.")
+  }
+
+  function(x) {
+    values <- safe_eval_density(density_fn, x)
+    if (log_input) {
+      if (any(values == Inf)) {
+        stop("Log-density returned +Inf values.")
+      }
+      return(values)
+    }
+
+    if (any(values < 0)) {
+      stop("Density function returned negative values.")
+    }
+    if (any(is.infinite(values) & values > 0)) {
+      stop("Density function returned +Inf values.")
+    }
+    log(values)
+  }
+}
