+validate_inputs <- function(n, f, domain, initial_points, max_iter, concavity_tol, deriv_eps) {
+  if (!is.numeric(n) || length(n) != 1L || is.na(n) || n <= 0 || n != floor(n)) {
+    stop("`n` must be a positive integer.")
+  }
+  if (n > .Machine$integer.max) {
+    stop("`n` is too large.")
+  }
+
+  valid_f <- is.function(f) ||
+    (is.character(f) && length(f) == 1L) ||
+    (is.expression(f) && length(f) == 1L) ||
+    is.language(f)
+  if (!valid_f) {
+    stop("`f` must be a function or expression-like object.")
+  }
+
+  if (!is.numeric(domain) || length(domain) != 2L || any(is.na(domain))) {
+    stop("`domain` must be a numeric vector of length 2.")
+  }
+  if (!(domain[1] < domain[2])) {
+    stop("`domain[1]` must be strictly smaller than `domain[2]`.")
+  }
+
+  if (!is.null(initial_points)) {
+    if (!is.numeric(initial_points) || any(is.na(initial_points))) {
+      stop("`initial_points` must be numeric when supplied.")
+    }
+    if (length(unique(initial_points)) < 3L) {
+      stop("`initial_points` must contain at least 3 unique values.")
+    }
+  }
+
+  if (!is.numeric(max_iter) || length(max_iter) != 1L || is.na(max_iter) ||
+      max_iter <= 0 || max_iter != floor(max_iter)) {
+    stop("`max_iter` must be a positive integer.")
+  }
+  if (!is.numeric(concavity_tol) || length(concavity_tol) != 1L || is.na(concavity_tol) ||
+      concavity_tol <= 0) {
+    stop("`concavity_tol` must be a positive number.")
+  }
+  if (!is.numeric(deriv_eps) || length(deriv_eps) != 1L || is.na(deriv_eps) || deriv_eps <= 0) {
+    stop("`deriv_eps` must be a positive number.")
+  }
+}
