+default_initial_points <- function(domain) {
+  lower <- domain[1]
+  upper <- domain[2]
+
+  if (is.finite(lower) && is.finite(upper)) {
+    return(lower + (upper - lower) * c(0.2, 0.5, 0.8))
+  }
+  if (is.finite(lower) && is.infinite(upper)) {
+    return(lower + c(0.5, 1.5, 3.0))
+  }
+  if (is.infinite(lower) && is.finite(upper)) {
+    return(upper - c(3.0, 1.5, 0.5))
+  }
+  c(-1.0, 0.0, 1.0)
+}
+
+candidate_initial_grid <- function(domain) {
+  lower <- domain[1]
+  upper <- domain[2]
+
+  if (is.finite(lower) && is.finite(upper)) {
+    return(seq(lower, upper, length.out = 200L))
+  }
+  if (is.finite(lower) && is.infinite(upper)) {
+    return(c(lower + seq(0.01, 2, length.out = 80L), lower + seq(2.5, 25, length.out = 120L)))
+  }
+  if (is.infinite(lower) && is.finite(upper)) {
+    return(c(upper - seq(25, 2.5, length.out = 120L), upper - seq(2, 0.01, length.out = 80L)))
+  }
+  seq(-10, 10, length.out = 200L)
+}
