+initialize_support_points <- function(log_density_fn, domain, initial_points = NULL) {
+  lower <- domain[1]
+  upper <- domain[2]
+
+  if (is.null(initial_points)) {
+    support_x <- sort(unique(default_initial_points(domain)))
+  } else {
+    support_x <- sort(unique(as.numeric(initial_points)))
+  }
+
+  if (is.finite(lower) && any(support_x <= lower)) {
+    stop("All support points must be strictly inside the lower domain bound.")
+  }
+  if (is.finite(upper) && any(support_x >= upper)) {
+    stop("All support points must be strictly inside the upper domain bound.")
+  }
+
+  support_h <- log_density_fn(support_x)
+  has_bad <- any(!is.finite(support_h))
+
+  if (has_bad && !is.null(initial_points)) {
+    stop("`initial_points` must have finite log-density values.")
+  }
+
+  if (has_bad && is.null(initial_points)) {
+    grid <- candidate_initial_grid(domain)
+    grid_h <- log_density_fn(grid)
+    finite_x <- grid[is.finite(grid_h)]
+    if (length(finite_x) < 3L) {
+      stop("Unable to find valid initial support points in the specified domain.")
+    }
+    idx <- unique(round(seq(1, length(finite_x), length.out = 3L)))
+    if (length(idx) < 3L) {
+      stop("Unable to derive 3 distinct finite initial support points.")
+    }
+    support_x <- sort(unique(finite_x[idx]))
+    support_h <- log_density_fn(support_x)
+  }
+
+  if (length(support_x) < 3L || any(!is.finite(support_h))) {
+    stop("Need at least 3 valid, finite support points to initialize ARS.")
+  }
+
+  list(x = support_x, h = support_h)
+}
