+estimate_derivative <- function(log_density_fn, x, domain, eps = 1e-4) {
+  lower <- domain[1]
+  upper <- domain[2]
+  base_h <- log_density_fn(x)
+  deriv <- numeric(length(x))
+
+  for (i in seq_along(x)) {
+    xi <- x[i]
+    step <- eps * max(1.0, abs(xi))
+
+    left <- xi - step
+    right <- xi + step
+
+    can_left <- !is.finite(lower) || (left > lower)
+    can_right <- !is.finite(upper) || (right < upper)
+
+    if (can_left && can_right) {
+      hl <- log_density_fn(left)
+      hr <- log_density_fn(right)
+      deriv[i] <- (hr - hl) / (2.0 * step)
+    } else if (can_right) {
+      if (is.finite(upper)) {
+        right <- min(right, upper - 1e-10)
+      }
+      hr <- log_density_fn(right)
+      deriv[i] <- (hr - base_h[i]) / (right - xi)
+    } else if (can_left) {
+      if (is.finite(lower)) {
+        left <- max(left, lower + 1e-10)
+      }
+      hl <- log_density_fn(left)
+      deriv[i] <- (base_h[i] - hl) / (xi - left)
+    } else {
+      stop("Unable to estimate derivative near a domain boundary.")
+    }
+  }
+
+  if (any(!is.finite(deriv))) {
+    stop("Derivative estimation failed due to non-finite values.")
+  }
+  deriv
+}
