+check_log_concavity_support <- function(x, h, tol = 1e-6) {
+  if (length(x) != length(h) || length(x) < 3L) {
+    return(TRUE)
+  }
+  if (any(diff(x) <= 0) || any(!is.finite(h))) {
+    return(FALSE)
+  }
+
+  slopes <- diff(h) / diff(x)
+  all(diff(slopes) <= tol)
+}
