+log_sum_exp <- function(log_values) {
+  m <- max(log_values)
+  if (!is.finite(m)) {
+    return(m)
+  }
+  m + log(sum(exp(log_values - m)))
+}
+
+log_abs_diff_exp <- function(a, b) {
+  if (a == b) {
+    return(-Inf)
+  }
+  m <- max(a, b)
+  n <- min(a, b)
+  if (!is.finite(m)) {
+    return(m)
+  }
+  delta <- exp(n - m)
+  if (delta >= 1.0) {
+    return(-Inf)
+  }
+  m + log1p(-delta)
+}
