+segment_log_integral <- function(hx, slope, x0, left, right) {
+  if (!(left < right)) {
+    stop("Invalid segment limits when integrating upper hull.")
+  }
+
+  if (abs(slope) < 1e-12) {
+    if (is.infinite(left) || is.infinite(right)) {
+      return(Inf)
+    }
+    return(hx + log(right - left))
+  }
+
+  if (is.infinite(left) && is.infinite(right)) {
+    return(Inf)
+  }
+
+  if (is.infinite(right)) {
+    if (slope >= 0) {
+      return(Inf)
+    }
+    return(hx + slope * (left - x0) - log(-slope))
+  }
+
+  if (is.infinite(left)) {
+    if (slope <= 0) {
+      return(Inf)
+    }
+    return(hx + slope * (right - x0) - log(slope))
+  }
+
+  a <- slope * (left - x0)
+  b <- slope * (right - x0)
+  log_diff <- log_abs_diff_exp(a, b)
+  hx + log_diff - log(abs(slope))
+}
+
+sample_from_segment <- function(left, right, slope) {
+  if (abs(slope) < 1e-12) {
+    if (is.infinite(left) || is.infinite(right)) {
+      stop("Cannot sample flat segment with infinite width.")
+    }
+    return(runif(1L, min = left, max = right))
+  }
+
+  if (is.infinite(right)) {
+    if (slope >= 0) {
+      stop("Invalid right-infinite segment with non-negative slope.")
+    }
+    return(left + rexp(1L, rate = -slope))
+  }
+
+  if (is.infinite(left)) {
+    if (slope <= 0) {
+      stop("Invalid left-infinite segment with non-positive slope.")
+    }
+    return(right - rexp(1L, rate = slope))
+  }
+
+  a <- slope * left
+  b <- slope * right
+  m <- max(a, b)
+  exp_a <- exp(a - m)
+  exp_b <- exp(b - m)
+  u <- runif(1L)
+  t <- exp_a + u * (exp_b - exp_a)
+  if (t <= 0) {
+    stop("Numerical instability while sampling a hull segment.")
+  }
+  (m + log(t)) / slope
+}
