+build_upper_hull <- function(x, h, hp, domain, concavity_tol) {
+  ord <- order(x)
+  x <- x[ord]
+  h <- h[ord]
+  hp <- hp[ord]
+  k <- length(x)
+
+  if (any(diff(x) <= 0)) {
+    stop("Support points must be strictly increasing.")
+  }
+  if (is.infinite(domain[1]) && hp[1] <= 0) {
+    stop("Non-log-concavity or bad initialization: left tail does not yield integrable hull.")
+  }
+  if (is.infinite(domain[2]) && hp[k] >= 0) {
+    stop("Non-log-concavity or bad initialization: right tail does not yield integrable hull.")
+  }
+
+  z <- numeric(k + 1L)
+  z[1L] <- domain[1]
+  z[k + 1L] <- domain[2]
+
+  for (i in seq_len(k - 1L)) {
+    denom <- hp[i] - hp[i + 1L]
+    if (abs(denom) < 1e-12) {
+      zi <- 0.5 * (x[i] + x[i + 1L])
+    } else {
+      zi <- ((h[i + 1L] - h[i]) - x[i + 1L] * hp[i + 1L] + x[i] * hp[i]) / denom
+    }
+    z[i + 1L] <- zi
+    if (!(z[i] < z[i + 1L])) {
+      stop("Non-log-concavity detected: tangent intersections are not increasing.")
+    }
+  }
+
+  log_weights <- numeric(k)
+  for (i in seq_len(k)) {
+    log_weights[i] <- segment_log_integral(h[i], hp[i], x[i], z[i], z[i + 1L])
+    if (!is.finite(log_weights[i])) {
+      stop("Upper hull is not integrable on this domain.")
+    }
+  }
+
+  log_total <- log_sum_exp(log_weights)
+  if (!is.finite(log_total)) {
+    stop("Invalid upper hull normalization.")
+  }
+
+  probs <- exp(log_weights - log_total)
+  cum_probs <- cumsum(probs)
+  cum_probs[length(cum_probs)] <- 1.0
+
+  if (any(diff(cum_probs) < -concavity_tol)) {
+    stop("Numerical issue constructing cumulative hull probabilities.")
+  }
+
+  list(x = x, h = h, hp = hp, z = z, log_weights = log_weights, cum_probs = cum_probs)
+}
+
+sample_from_upper_hull <- function(hull) {
+  u <- runif(1L)
+  seg <- which(hull$cum_probs >= u)[1L]
+  if (is.na(seg)) {
+    seg <- length(hull$cum_probs)
+  }
+
+  left <- hull$z[seg]
+  right <- hull$z[seg + 1L]
+  slope <- hull$hp[seg]
+
+  x <- sample_from_segment(left, right, slope)
+  upper_val <- hull$h[seg] + slope * (x - hull$x[seg])
+
+  list(x = x, segment = seg, upper = upper_val)
+}
