+insert_support_point <- function(x_new, h_new, support_x, support_h, tol = 1e-12) {
+  if (any(abs(support_x - x_new) < tol)) {
+    return(list(x = support_x, h = support_h, added = FALSE))
+  }
+
+  x_all <- c(support_x, x_new)
+  h_all <- c(support_h, h_new)
+  ord <- order(x_all)
+  list(x = x_all[ord], h = h_all[ord], added = TRUE)
+}
