+ars <- function(
+    n,
+    f,
+    domain = c(-Inf, Inf),
+    initial_points = NULL,
+    log_input = FALSE,
+    max_iter = 100000L,
+    concavity_tol = 1e-6,
+    deriv_eps = 1e-4,
+    seed = NULL
+) {
+  validate_inputs(n, f, domain, initial_points, max_iter, concavity_tol, deriv_eps)
+
+  if (!is.null(seed)) {
+    if (!is.numeric(seed) || length(seed) != 1L || is.na(seed)) {
+      stop("`seed` must be NULL or a single numeric value.")
+    }
+    set.seed(as.integer(seed))
+  }
+
+  density_fn <- as_density_function(f, eval_env = parent.frame())
+  log_density_fn <- make_log_density_function(density_fn, log_input = log_input)
+
+  support <- initialize_support_points(log_density_fn, domain, initial_points)
+  if (!check_log_concavity_support(support$x, support$h, tol = concavity_tol)) {
+    stop("Initial support indicates a non-log-concave density.")
+  }
+
+  n <- as.integer(n)
+  max_iter <- as.integer(max_iter)
+  samples <- numeric(n)
+  accepted <- 0L
+  iter <- 0L
+
+  while (accepted < n) {
+    iter <- iter + 1L
+    if (iter > max_iter) {
+      stop("Maximum iterations reached before obtaining the requested samples.")
+    }
+
+    hp <- estimate_derivative(log_density_fn, support$x, domain, eps = deriv_eps)
+    hull <- build_upper_hull(support$x, support$h, hp, domain, concavity_tol)
+    cand <- sample_from_upper_hull(hull)
+    x_cand <- cand$x
+
+    lower_val <- eval_lower_hull(x_cand, support$x, support$h)
+    if (is.finite(lower_val) && (log(runif(1L)) <= (lower_val - cand$upper))) {
+      accepted <- accepted + 1L
+      samples[accepted] <- x_cand
+      next
+    }
+
+    h_cand <- log_density_fn(x_cand)
+    if (!is.finite(h_cand)) {
+      next
+    }
+
+    if ((h_cand - cand$upper) > concavity_tol) {
+      stop("Non-log-concavity detected during sampling: log-density exceeded upper hull.")
+    }
+
+    if (log(runif(1L)) <= (h_cand - cand$upper)) {
+      accepted <- accepted + 1L
+      samples[accepted] <- x_cand
+    }
+
+    strictly_inside <- (!is.finite(domain[1]) || x_cand > domain[1]) &&
+      (!is.finite(domain[2]) || x_cand < domain[2])
+    if (strictly_inside) {
+      updated <- insert_support_point(x_cand, h_cand, support$x, support$h)
+      support$x <- updated$x
+      support$h <- updated$h
+      if (!check_log_concavity_support(support$x, support$h, tol = concavity_tol)) {
+        stop("Non-log-concavity detected during adaptation of support points.")
+      }
+    }
+  }
+
+  samples
+}
