+print_test_result <- function(name, passed, mean_value = NA_real_, sd_value = NA_real_) {
+  status <- if (isTRUE(passed)) "PASS" else "FAIL"
+  cat(sprintf(
+    "%s: %s (mean=%s, sd=%s)\n",
+    name,
+    status,
+    format_stat(mean_value),
+    format_stat(sd_value)
+  ))
+}
