+test <- function() {
+  results <- list()
+
+  # Module test: concavity detector on known concave/non-concave shapes.
+  concave_ok <- check_log_concavity_support(c(-2, 0, 2), c(-4, 0, -4))
+  non_concave_ok <- !check_log_concavity_support(c(-2, 0, 2), c(4, 0, 4))
+  module_pass <- concave_ok && non_concave_ok
+  print_test_result("MODULE_CONCAVITY_CHECK", module_pass, NA_real_, NA_real_)
+  results[["MODULE_CONCAVITY_CHECK"]] <- module_pass
+
+  normal_res <- tryCatch({
+    set.seed(123)
+    s <- ars(
+      n = 5000,
+      f = dnorm,
+      domain = c(-Inf, Inf),
+      initial_points = c(-2, 0, 2),
+      max_iter = 200000L
+    )
+    m <- mean(s)
+    sdv <- sd(s)
+    pass <- abs(m) < 0.08 && abs(sdv - 1.0) < 0.08
+    write.table(s, file = "/app/normal_samples.txt", row.names = FALSE, col.names = FALSE)
+    list(pass = pass, mean = m, sd = sdv)
+  }, error = function(e) {
+    list(pass = FALSE, mean = NA_real_, sd = NA_real_, error = e$message)
+  })
+  print_test_result("NORMAL_DISTRIBUTION", normal_res$pass, normal_res$mean, normal_res$sd)
+  results[["NORMAL_DISTRIBUTION"]] <- normal_res
+
+  exp_res <- tryCatch({
+    set.seed(456)
+    s <- ars(
+      n = 5000,
+      f = function(x) dexp(x, rate = 2),
+      domain = c(0, Inf),
+      initial_points = c(0.25, 1, 2.5),
+      max_iter = 200000L
+    )
+    m <- mean(s)
+    sdv <- sd(s)
+    pass <- abs(m - 0.5) < 0.05 && abs(sdv - 0.5) < 0.05
+    list(pass = pass, mean = m, sd = sdv)
+  }, error = function(e) {
+    list(pass = FALSE, mean = NA_real_, sd = NA_real_, error = e$message)
+  })
+  print_test_result("EXPONENTIAL_DISTRIBUTION", exp_res$pass, exp_res$mean, exp_res$sd)
+  results[["EXPONENTIAL_DISTRIBUTION"]] <- exp_res
+
+  expr_res <- tryCatch({
+    set.seed(789)
+    s <- ars(
+      n = 3000,
+      f = expression(exp(-x^2 / 2)),
+      domain = c(-Inf, Inf),
+      initial_points = c(-2, 0, 2),
+      max_iter = 200000L
+    )
+    m <- mean(s)
+    sdv <- sd(s)
+    pass <- abs(m) < 0.10 && abs(sdv - 1.0) < 0.10
+    list(pass = pass, mean = m, sd = sdv)
+  }, error = function(e) {
+    list(pass = FALSE, mean = NA_real_, sd = NA_real_, error = e$message)
+  })
+  print_test_result("EXPRESSION_INPUT", expr_res$pass, expr_res$mean, expr_res$sd)
+  results[["EXPRESSION_INPUT"]] <- expr_res
+
+  invalid_n_pass <- FALSE
+  tryCatch({
+    ars(n = -10, f = dnorm)
+  }, error = function(e) {
+    invalid_n_pass <<- TRUE
+  })
+  print_test_result("INVALID_N_INPUT", invalid_n_pass, NA_real_, NA_real_)
+  results[["INVALID_N_INPUT"]] <- invalid_n_pass
+
+  invalid_domain_pass <- FALSE
+  tryCatch({
+    ars(n = 10, f = dnorm, domain = c(1, -1))
+  }, error = function(e) {
+    invalid_domain_pass <<- TRUE
+  })
+  print_test_result("INVALID_DOMAIN_INPUT", invalid_domain_pass, NA_real_, NA_real_)
+  results[["INVALID_DOMAIN_INPUT"]] <- invalid_domain_pass
