+    }
+    return(runif(1L, min = left, max = right))
+  }
+
+  if (is.infinite(right)) {
+    if (slope >= 0) {
+      stop("Invalid right-infinite segment with non-negative slope.")
+    }
+    return(left + rexp(1L, rate = -slope))
+  }
+
+  if (is.infinite(left)) {
+    if (slope <= 0) {
+      stop("Invalid left-infinite segment with non-positive slope.")
+    }
+    return(right - rexp(1L, rate = slope))
+  }
