+  outside <- (x < support_x[1L]) | (x > support_x[k])
+  if (all(outside)) {
+    return(result)
+  }
+
+  idx <- findInterval(x, support_x, rightmost.closed = TRUE)
+  idx[idx >= k] <- k - 1L
+
+  valid <- !outside & idx >= 1L & idx < k
+  if (any(valid)) {
+    j <- idx[valid]
+    x0 <- support_x[j]
+    x1 <- support_x[j + 1L]
+    h0 <- support_h[j]
+    h1 <- support_h[j + 1L]
+    w <- (x[valid] - x0) / (x1 - x0)
+    result[valid] <- h0 + w * (h1 - h0)
+  }
