import time, numpy as np
ul=np.linalg._umath_linalg

N=4000
sizes=[2,4,6,8,10]

def ref(A):
    w,v=np.linalg.eig(A)
    i=np.abs(w).argmax()
    return w[i],v[:,i]

def fast1(A):
    w,v=ul.eig(A,signature='d->DD')
    i=np.abs(w).argmax()
    return w[i],v[:,i]

def fast2(A):
    a=np.asarray(A,dtype=np.float64)
    w,v=ul.eig(a,signature='d->DD')
    i=np.abs(w).argmax()
    return w[i],v[:,i]

def fast3(A):
    w,v=ul.eig(A,signature='d->DD')
    i=(w.real*w.real + w.imag*w.imag).argmax()
    return w[i],v[:,i]

def check(fn):
    for n in sizes:
        for _ in range(1000):
            A=np.random.normal(size=(n,n)).astype(np.float64)
            l,x=fn(A)
            if not np.allclose(A@x,l*x):
                return False,n
    return True,None

for name,fn in [('ref',ref),('fast1',fast1),('fast2',fast2),('fast3',fast3)]:
    print(name,'check',check(fn))

for n in sizes:
    mats=[np.random.normal(size=(n,n)).astype(np.float64) for _ in range(N)]
    print('n',n)
    for name,fn in [('ref',ref),('fast1',fast1),('fast2',fast2),('fast3',fast3)]:
        t0=time.perf_counter()
        for A in mats:
            fn(A)
        dt=(time.perf_counter()-t0)/N
        print(name,dt)
    print('---')
