{"cmd":"ref=2025-08-12; start7=$(date -d "$ref -6 days" +%F); start30=$(date -d "$ref -29 days" +%F); mstart=$(date -d "$ref" +%Y-%m-01); awk -v ref="$ref" -v start7="$start7" -v start30="$start30" -v mstart="$mstart" '
BEGIN {
  print "period,severity,count"
  periods[1] = "today"
  periods[2] = "last_7_days"
  periods[3] = "last_30_days"
  periods[4] = "month_to_date"
  periods[5] = "total"
  sevs[1] = "ERROR"
  sevs[2] = "WARNING"
  sevs[3] = "INFO"
  for (i = 1; i <= 5; i++) {
    for (j = 1; j <= 3; j++) {
      c[periods[i], sevs[j]] = 0
    }
  }
}
{
  if (match($0, /\[(ERROR|WARNING|INFO)\]/, m)) {
    sev = m[1]
    fname = FILENAME
    sub(/^.*\//, "", fname)
    d = substr(fname, 1, 10)

    c["total", sev]++
    if (d == ref) c["today", sev]++
    if (d >= start7 && d <= ref) c["last_7_days", sev]++
    if (d >= start30 && d <= ref) c["last_30_days", sev]++
    if (d >= mstart && d <= ref) c["month_to_date", sev]++
  }
}
END {
  for (i = 1; i <= 5; i++) {
    for (j = 1; j <= 3; j++) {
      printf "%s,%s,%d\n", periods[i], sevs[j], c[periods[i], sevs[j]]
    }
  }
}
' /app/logs/*.log > /app/summary.csv; cat /app/summary.csv"}

